import React, { useState, useEffect } from 'react';
import Parser from 'rss-parser';

const CORS_PROXY = 'https://cors-anywhere.herokuapp.com/';
interface RssItem {
    title: string;
    link: string;
    description: string;
    pubDate: string;
}
const Page: React.FC = () => {
    const [rssItems, setRssItems] = useState<RssItem[]>([]);

    function FetchDataFromRssFeed() {
        const request = new XMLHttpRequest();
        request.onreadystatechange = () => {
            if (request.readyState === 4 && request.status === 200) {
                const parser = new DOMParser();
                const xml = parser.parseFromString(request.responseText, 'text/xml');
                const items = xml.querySelectorAll('item');
                const rssItemsArray: RssItem[] = Array.from(items, (item) => ({
                    title: item.querySelector('title')?.textContent || '',
                    link: item.querySelector('link')?.textContent || '',
                    description: item.querySelector('description')?.textContent || '',
                    pubDate: item.querySelector('pubDate')?.textContent || '',
                }));

                setRssItems(rssItemsArray);
            }
        };
        request.open('GET', 'https://nld.com.vn/rss/home.rss', true);
        request.send();
    }

    useEffect(() => {
        FetchDataFromRssFeed()
    },[]);

    return (
        <div>
            {rssItems.length === 0 ? (
                <div>Đang tải...</div>
            ) : (
                rssItems.map((item, index) => (
                    <div key={index} className="rss-item">
                        <h2><a href={item.link}>{item.title}</a></h2>
                        <p>{item.description}</p>
                        <p><em>{item.pubDate}</em></p>
                        <hr/>
                    </div>
                ))
            )}
        </div>
    )
}
export default Page
