import React, { useState, useEffect } from 'react';

function BackToTop() {
    const [isVisible, setIsVisible] = useState(false);

    if (window.scrollY > 200) {
        setIsVisible(true);
    } else {
        setIsVisible(false);
    }

    const scrollToTop = () => {
        window.scrollTo({
            top: 0,
            behavior: 'smooth'
        });
    };

    return (
        isVisible && (<div id="backToTop">^</div>)
    )
}

export default BackToTop;
