import React, { useState, useEffect } from 'react';

const CORS_PROXY = 'https://cors-anywhere.herokuapp.com/';
type Props = {}
interface RssItem {
    title: string;
    link: string;
    description: string;
    pubDate: string;
}
const News: React.FC<{ url: string, quantity: number }> = ({ url,quantity }) => {
    const [rssItems, setRssItems] = useState<RssItem[]>([]);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await fetch(`http://localhost:3002/rss?url=${encodeURIComponent(url)}`);
                if (!response.ok) {
                    throw new Error('Không thể tải dữ liệu RSS');
                }

                const data = await response.json();
                if(quantity!==-1) {
                    setRssItems(data.slice(0,quantity));
                } else {
                    setRssItems(data);
                }
            } catch (error) {
                console.error('Lỗi khi lấy dữ liệu RSS:', error);
            }
        };

        fetchData();
    }, [url]);

    return (
        <>
            {rssItems.length === 0 ? (
                <p>Đang tải dữ liệu...</p>
            ) : (
                <>
                    {rssItems.map((item, index) => (
                        <div data-id={`${index}`} key={index}>
                            <h2><a href={item.link} target="_blank" rel="noopener noreferrer">{item.title}</a></h2>
                            <div dangerouslySetInnerHTML={{ __html: item.description }} />
                        </div>
                    ))}
                </>
            )}
        </>
    );
};

export default News;
