import React, { useState, useEffect } from 'react';

const BackToTop: React.FC = () => {
    const [isVisible, setIsVisible] = useState(false);

    if (window.scrollY > 200) {
        setIsVisible(true);
    } else {
        setIsVisible(false);
    }

    const scrollToTop = () => {
        window.scrollTo({
            top: 0,
            behavior: 'smooth'
        });
    };

    return (
        <>
            {isVisible &&
                <div onClick={scrollToTop} id="backToTop">
                    â†‘ Back to Top
                </div>
            }
        </>
    )
}

export default BackToTop;
